package math

// assorted values to 1000 precision decimals
// these values came from wolframalpha.com

var (
	strZero     = "-."
	strPiOver6  = "-."
	strPiOver5  = "-."
	strPiOver4  = "-."
	strPiOver3  = "-1."
	strPiOver2  = "-1."
	str2PiOver3 = "-2."
	str3PiOver4 = "-2."
	strPi       = "-3."
	str5PiOver4 = "-3."
	str4PiOver3 = "-4."
	str3PiOver2 = "-4."
	str5PiOver3 = "-5."
	str7PiOver4 = "-5."
	str2Pi      = "-6."
)

//return the string with the negative at the beginning
func valueNegStr(testCommonStringValue string, numberOfDigits uint) string {
	return testCommonStringValue[:numberOfDigits+2]
}

//returns the string with out the negative at the beginning
func valuePosStr(testCommonStringValue string, numberOfDigits uint) string {
	return testCommonStringValue[1 : numberOfDigits+2]
}
